var express=require("express"),router=express.Router(),mongoose=require("mongoose"),Project=mongoose.model("Project");router.get("/",function(e,n,o){Project.find(function(e,o){n.render("admin/index",{title:"Admin",projects:o})})}),router.post("/create_project",function(e,n){var o=e.body.name;console.log(o),new Project({name:o,deleted:!1}).save(function(e,o){e||n.send(o._id)})}),router.post("/create_sub_project",function(e,n){var o=e.body.name,r=e.body.parentId;Project.findById(r,function(e,t){if(e)console.log(e);else{var d=new Project({name:o,deleted:!1,hasParent:!0,parentId:r,parentName:t.name});d.save(),console.log(t),t.children.push(d._id),t.hasChildren=!0,t.save(function(e,o){n.send("success")})}})}),router.get("/delete/:id",function(e,n){var o=e.params.id;Project.findById(o,function(e,o){o.deleted=!0,o.save(function(e,o){n.redirect("/admin")})})}),router.get("/project/:id",function(e,n,o){Project.find(function(o,r){n.render("admin/index",{title:"Admin",projects:r,currentProjectId:e.params.id})})}),router.post("/save_all",function(e,n){var o=e.body,r=o.id,t=o.description;Project.findById(r,function(e,o){o.description=t,o.save(function(e){e?n.send(e):n.send("success")})})}),module.exports=router;