var express=require("express"),router=express.Router(),mongoose=require("mongoose"),Project=mongoose.model("Project");router.get("/",function(e,r,o){Project.find(function(e,o){r.render("admin/index",{title:"Admin",projects:o})})}),router.post("/create_project",function(e,r){var o=e.body.name;console.log(o),new Project({name:o,deleted:!1}).save(function(e,o){e||r.send(o._id)})}),router.get("/delete/:id",function(e,r){var o=e.params.id;Project.findById(o,function(e,o){o.deleted=!0,o.save(function(e,o){r.redirect("/admin")})})}),router.get("/project/:id",function(e,r,o){Project.find(function(o,t){r.render("admin/index",{title:"Admin",projects:t,currentProjectId:e.params.id})})}),module.exports=router;